  }

  onLogOut(){
    window.location ="/login"
  }


render() {
return (
    <React.Fragment>
      <Typography variant={"subtitle1"}>
        My name is <b>{this.props.displayName}</b>
      </Typography>
      <Typography variant={"subtitle1"}>
        My name is <b>{this.props.id}</b>
      </Typography>
      <Typography variant={"subtitle1"}>
        Pantry: <b>{this.props.activeUser.data.pantry.join("\n ")}</b>
      </Typography>

      <Button variant={"contained"} onClick={() => fireAuth.signOut()}>
        Logout
      </Button>
    </React.Fragment>
  );
}
}
export default Profile;
