import React, { Component } from "react";
import Button from "@material-ui/core/Button";
import TextField from "@material-ui/core/TextField";
import Avatar from "@material-ui/core/Avatar";
import Grid from "@material-ui/core/Grid";
import Typography from "@material-ui/core/Typography";
import { fireAuth } from "../fireApi";
import { render } from "react-dom";
import API from '../utils/API';

class Profile extends Component {

  // constructor(props) {
  //   super(props);
   state = {
  _id: this.props.activeUser.data._id,
  username: "",
  email: "",
  fBaseId: this.props.activeUser.data.fBaseId,
  pantry: [...this.props.activeUser.data.pantry, ""],
  favRecipes: this.props.activeUser.data.favRecipes
};
// console.log(this.state)

// }

componentDidMount() {
  // this.loadUser(this.state.fBaseId)
  this.setState({  username: this.props.activeUser.data.username,
    email: this.props.activeUser.data.email})

}

loadUser = (uid) => {
  API.getUser(uid)
    .then(res =>
      this.setState({ activeUser: this.props.activeUser.data })
    )
    .catch(err => console.log(err));
};

handleInputChange = event => {
  const { name, value } = event.target;
  this.setState({
    [name]: value
  });
};

handleFormSubmit = event => {
  event.preventDefault();
  if (this.state.fBaseId) {
    API.addPantry(this.state._id)
      .then(res => this.loadUser(this.state.fBaseId))
      .catch(err => console.log(err));
  }
};


onLogOut(){
  window.location = "/"
}


render() {
  return (
    <React.Fragment>
      <Typography variant={"subtitle1"}>
        My name is <b>{this.props.displayName}</b>
      </Typography>
      <Typography variant={"subtitle1"}>
        My name is <b>{this.props.id}</b>
      </Typography>
      <Typography variant={"subtitle1"}>
        Name: <b>{this.props.activeUser.data.username}</b>
      </Typography>

      <Typography variant={"subtitle1"}>
        Pantry: <b>{this.props.activeUser.data.pantry.join("\n ")}</b>
      </Typography>

      <form >
        <TextField
          value={this.state.pantry}
          onChange={this.handleInputChange}
          name="pantry"
          placeholder="Add to pantry"
        />
        <Button onClick={this.handleFormSubmit}>
          Submit Book
        </Button>

      </form>

      <Button variant={"contained"} onClick={() => fireAuth.signOut()} onClick={this.onLogOut}>
        Logout
      </Button>
    </React.Fragment>
  );
}
}
export default Profile;
