import React, { Component } from "react";
import Button from "@material-ui/core/Button";
import Avatar from "@material-ui/core/Avatar";
import Grid from "@material-ui/core/Grid";
import Typography from "@material-ui/core/Typography";
import { fireAuth } from "../fireApi";
import { render } from "react-dom";

class Profile extends Component {

  constructor(props) {
    super(props);
    this.state = {
      activeUser: []
    };
    console.log(this.state)
  }

  componentDidMount() {
    this.setState({ activeUser: this.props.activeUser.data })
  }

  handleInputChange = event => {
    const { name, value } = event.target;
    this.setState({
      [name]: value
    });
  };


  onLogOut(){
    window.location ="/"
  }


render() {
return (
    <React.Fragment>
      <Typography variant={"subtitle1"}>
        My name is <b>{this.props.displayName}</b>
      </Typography>
      <Typography variant={"subtitle1"}>
        My name is <b>{this.props.id}</b>
      </Typography>
      <Typography variant={"subtitle1"}>
        Name: <b>{this.props.activeUser.data.username}</b>
      </Typography>

      <Typography variant={"subtitle1"}>
        Pantry: <b>{this.props.activeUser.data.pantry.join("\n ")}</b>
      </Typography>

      <form >
      <TextField
                value={this.state.pantry}
                onChange={this.handleInputChange}
                name="pantry"
                placeholder="Add to pantry"
              />
                <Button

                onClick={this.handleFormSubmit}
              >
                Submit Book
              </Button>

            </form>

      <Button variant={"contained"} onClick={() => fireAuth.signOut()} onClick = {this.onLogOut}>
        Logout
      </Button>
    </React.Fragment>
  );
}
}
export default Profile;
