import React, { Component } from "react";
import Button from "@material-ui/core/Button";
// import TextField from "@material-ui/core/TextField";

import Typography from "@material-ui/core/Typography";
import { fireAuth } from "../fireApi";

import API from '../utils/API';

class Profile extends Component {
state = {
  _id: this.props.activeUser.data._id,
  username: this.props.activeUser.data.username,
  email: this.props.activeUser.data.email,
  fBaseId: this.props.activeUser.data.fBaseId,
  pantry: [...this.props.activeUser.data.pantry, ""],
  favRecipes: this.props.activeUser.data.favRecipes
}

// this.handleInputChange = this.handleInputChange.bind(this);
// this.onChangePantry = this.onChangePantry.bind(this);
// console.log(this.state)



componentWillMount() {
  // this.loadUser(this.state.fBaseId)

}

loadUser = (uid) => {
  API.getUser(uid)
    .then(res =>
      this.setState({ activeUser: this.props.activeUser.data })
    )
    .catch(err => console.log(err));
};

handleInputChange = event => {

  const name = event.target.name;
  const value = event.target.value;

  this.setState({ [name]:value })
};

onChangePantry = event => {
  event.preventDefault();
    API.addPantry(this.state._id)
      .then(res => this.loadUser(this.state.fBaseId))
      .catch(err => console.log(err));

};


onLogOut(){
  window.location = "/"
}


render() {
  return (
    <React.Fragment>
      <Typography variant={"subtitle1"}>
        My name is <b>{this.props.displayName}</b>
      </Typography>
      <Typography variant={"subtitle1"}>
        My name is <b>{this.props.id}</b>
      </Typography>
      <Typography variant={"subtitle1"}>
        Name: <b>{this.props.activeUser.data.username}</b>
      </Typography>

      <Typography variant={"subtitle1"}>
        Pantry: <b>{this.props.activeUser.data.pantry.join("\n ")}</b>
      </Typography>

      <form >
        <input
          value={this.state.pantry}
          onChange={this.handleInputChange}
          name="pantry"
          type="text"
          placeholder="Add to pantry"
        />
        <Button onClick={this.onChangePantry} type="submit">
          Submit Book
        </Button>

      </form>

      <Button variant={"contained"} onClick={() => {fireAuth.signOut();this.onLogOut()}} >
        Logout
      </Button>
    </React.Fragment>
  );
}
}
export default Profile;
